def climb_stairs(n):
    if n &lt;= 2:
        return n

    dp = [0] * (n + 1)
    dp[1] = 1
    dp[2] = 2

    for i in range(3, n + 1):
        dp[i] = dp[i-1] + dp[i-2]

    return dp[n]

# Space optimized version
def climb_stairs_optimized(n):
    if n &lt;= 2:
        return n

    prev, curr = 1, 2
    for i in range(3, n + 1):
        prev, curr = curr, prev + curr

    return curr

class EventStore:
    def __init__(self):
        self.events = []

    def append_event(self, event):
        self.events.append(event)

    def get_events(self, aggregate_id):
        return [e for e in self.events if e.aggregate_id == aggregate_id]

    def get_aggregate(self, aggregate_id):
        events = self.get_events(aggregate_id)
        aggregate = Aggregate()
        for event in events:
            aggregate.apply(event)
        return aggregate

class Order:
    def __init__(self):
        self.id = None
        self.items = []
        self.status = "created"

    def apply(self, event):
        if isinstance(event, OrderCreated):
            self.id = event.order_id
        elif isinstance(event, ItemAdded):
            self.items.append(event.item)
        elif isinstance(event, OrderConfirmed):
            self.status = "confirmed"



class CommandHandler:
    def handle_create_order(self, command):
        # Write side - update write model
        order = Order(command.order_id, command.items)
        self.order_repository.save(order)

        # Publish event
        event = OrderCreated(command.order_id, command.items)
        self.event_bus.publish(event)

class QueryHandler:
    def get_order_summary(self, order_id):
        # Read side - query read model
        return self.order_summary_repository.find_by_id(order_id)

    def handle_order_created(self, event):
        # Update read model
        summary = OrderSummary(
            event.order_id,
            len(event.items),
            sum(item.price for item in event.items)
        )
        self.order_summary_repository.save(summary)

